mapper.update:
update sys_notice
notice_title=#{noticeTitle}, notice_type=#{noticeType}, notice_content=#{noticeContent}, status=#{status}, create_by=#{createBy}, create_time=#{createTime}, update_by=#{updateBy}, update_time=#{updateTime}, remark=#{remark}, notice_user_id=#{noticeUserId},
where notice_id=#{noticeId}

mapper.select:
select notice_id,
notice_type,
notice_content,
status,
create_by,
create_time,
update_by,
update_time,
remark,
notice_user_id from sys_dept

    notice_id=#{noticeId}
    notice_title=#{noticeTitle}
    notice_type=#{noticeType}
    notice_content=#{noticeContent}
    status=#{status}
    create_by=#{createBy}
    create_time=#{createTime}
    update_by=#{updateBy}
    update_time=#{updateTime}
    remark=#{remark}
    notice_user_id=#{noticeUserId}
